import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [motion, setMotion] = useState('');
  const [side, setSide] = useState('auto');
  const [timeLimit, setTimeLimit] = useState(5); // in minutes
  const [turns, setTurns] = useState([]);
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [aiSpeaking, setAiSpeaking] = useState(false);
  const [recorder, setRecorder] = useState(null);
  const [assignedSide, setAssignedSide] = useState(null);
  const [currentSpeaker, setCurrentSpeaker] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  // eslint-disable-next-line no-unused-vars
  const [recordingTimer, setRecordingTimer] = useState(null);


  useEffect(() => {
    // Load session if any
  }, []);

  const startDebate = async () => {
    try {
      const response = await axios.post('http://localhost:3001/start', { motion, side, timeLimit });
      setAssignedSide(response.data.assignedSide);

      if (response.data.assignedSide === 'opp') {
        // User is opposition, AI speaks first (as proposition)
        const aiData = JSON.parse(response.data.aiText);
        setTurns([{ speaker: 'ai', text: aiData.text, data: aiData }]);
        setCurrentSpeaker('ai');
        speak(aiData.text);
      } else {
        // User is proposition, user speaks first
        setTurns([]);
        setCurrentSpeaker('user');
      }
    } catch (error) {
      console.error('Start debate error:', error);
      alert('Failed to start debate: ' + (error.response?.data?.error || error.message));
    }
  };

  const record = () => {
    // Use MediaRecorder for recording
    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
      const recorder = new MediaRecorder(stream);
      setRecorder(recorder);
      recorder.start();
      setRecording(true);
      setRecordingTime(0);

      // Start timer for time limit
      const timer = setInterval(() => {
        setRecordingTime(prev => {
          const newTime = prev + 1;
          if (newTime >= timeLimit * 60) { // Convert minutes to seconds
            stopRecord();
            clearInterval(timer);
            alert('Time limit reached! Recording stopped.');
            return prev;
          }
          return newTime;
        });
      }, 1000);

      setRecordingTimer(timer);

      recorder.ondataavailable = e => setAudioBlob(e.data);
      recorder.onstop = () => {
        setRecording(false);
        clearInterval(timer);
        setRecordingTimer(null);
      };
    });
  };

  const stopRecord = async () => {
    if (recorder) {
      recorder.stop();
    }
    // Wait for blob to be ready (simplified)
    setTimeout(async () => {
      try {
        const formData = new FormData();
        formData.append('file', audioBlob);
        const response = await axios.post('http://localhost:3001/transcribe', formData);
        const userText = response.data.transcript;
        setTurns(prevTurns => [...prevTurns, { speaker: 'user', text: userText, audio: audioBlob }]);
        // Get AI response
        const aiResponse = await axios.post('http://localhost:3001/reply', { userText, turns: [...turns, { speaker: 'user', text: userText }] });
        const aiData = JSON.parse(aiResponse.data.text);
        setTurns(prevTurns => [...prevTurns, { speaker: 'user', text: userText, audio: audioBlob }, { speaker: 'ai', text: aiData.text, data: aiData }]);
        speak(aiData.text);
      } catch (error) {
        console.error('Recording error:', error);
        alert('Recording failed. Please try again.');
      }
    }, 500); // Delay to ensure blob is ready
  };

  const speak = (text) => {
    setAiSpeaking(true);
    let speechText = text;
    try {
      const parsed = JSON.parse(text);
      speechText = parsed.text; // Speak only the text field
    } catch (e) {
      // If not JSON, speak as is
    }
    const utterance = new SpeechSynthesisUtterance(speechText);
    // Improve voice quality and naturalness
    utterance.rate = 0.9; // Slightly slower for clarity
    utterance.pitch = 1.0; // Natural pitch
    utterance.volume = 0.8; // Good volume level

    // Try to use a more natural voice if available
    const voices = window.speechSynthesis.getVoices();
    const preferredVoice = voices.find(voice =>
      voice.name.includes('Female') || voice.name.includes('Zira') ||
      voice.name.includes('Google') || voice.lang.startsWith('en')
    );
    if (preferredVoice) {
      utterance.voice = preferredVoice;
    }

    utterance.onend = () => setAiSpeaking(false);
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="App">
      <div className="header">
        <h1>AI Debate Partner</h1>
        <div className="setup-panel">
          <div className="form-group">
            <label>Debate Motion</label>
            <input
              value={motion}
              onChange={e => setMotion(e.target.value)}
              placeholder="Enter your debate topic..."
            />
          </div>
          <div className="form-group">
            <label>Your Side</label>
            <select value={side} onChange={e => setSide(e.target.value)}>
              <option value="auto">Auto Assign Side</option>
              <option value="pro">Proposition</option>
              <option value="opp">Opposition</option>
            </select>
          </div>
          <div className="form-group">
            <label>Speech Time Limit (minutes)</label>
            <select value={timeLimit} onChange={e => setTimeLimit(parseInt(e.target.value))}>
              <option value={3}>3 minutes</option>
              <option value={5}>5 minutes</option>
              <option value={7}>7 minutes</option>
              <option value={10}>10 minutes</option>
            </select>
          </div>
          <button className="start-button" onClick={startDebate}>Start Debate</button>
        </div>
      </div>

      {turns.length > 0 && (
        <div className="debate-info">
          <div className="info-item">
            <h3>Motion</h3>
            <p>{motion}</p>
          </div>
          <div className="info-item">
            <h3>Your Side</h3>
            <p>{side === 'auto' ? (assignedSide === 'pro' ? 'Proposition' : 'Opposition') : side === 'pro' ? 'Proposition' : 'Opposition'}</p>
          </div>
          <div className="info-item">
            <h3>Speech Time Limit</h3>
            <p>{timeLimit} minutes</p>
          </div>
        </div>
      )}

      <div className="chat-container">
        <div className="chat">
          {turns.map((turn, i) => (
            <div key={i} className={`turn ${turn.speaker}`}>
              <div className="speaker-indicator">
                {turn.speaker === 'ai' ? 'ü§ñ AI' : 'üë§ You'}
              </div>
              <p>{turn.text}</p>
              {turn.speaker === 'ai' && <button className="play-button" onClick={() => speak(turn.text)}>üîä Play</button>}
              {turn.speaker === 'user' && turn.audio && <audio controls src={URL.createObjectURL(turn.audio)} />}
            </div>
          ))}
          {aiSpeaking && <div className="ai-speaking">ü§ñ AI is speaking...</div>}
        </div>
      </div>

      <div className="controls">
        {currentSpeaker === 'user' && !aiSpeaking && (
          <>
            <button className={`record-button ${recording ? 'recording' : ''}`} onClick={record} disabled={recording}>
              {recording ? (
                <>
                  Recording ({Math.floor(recordingTime / 60)}:{(recordingTime % 60).toString().padStart(2, '0')})
                  <div className="audio-visualizer">
                    <div className="audio-bar"></div>
                    <div className="audio-bar"></div>
                    <div className="audio-bar"></div>
                    <div className="audio-bar"></div>
                    <div className="audio-bar"></div>
                  </div>
                </>
              ) : (
                'üé§ Record Response'
              )}
            </button>
            <button className="stop-button" onClick={stopRecord} disabled={!recording}>‚èπÔ∏è Stop & Submit</button>
          </>
        )}
        {currentSpeaker === 'ai' && <div className="waiting-message">ü§ñ AI is preparing its response...</div>}
        {aiSpeaking && <div className="waiting-message">ü§ñ AI is speaking...</div>}
      </div>

    </div>
  );
}

export default App;
