import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [motion, setMotion] = useState('');
  const [side, setSide] = useState('auto');
  const [tone, setTone] = useState('Formal');
  const [turns, setTurns] = useState([]);
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [aiSpeaking, setAiSpeaking] = useState(false);
  const [recorder, setRecorder] = useState(null);
  const [assignedSide, setAssignedSide] = useState('');
  const [debateStarted, setDebateStarted] = useState(false);

  useEffect(() => {
    // Load session if any
  }, []);

  const startDebate = async () => {
    const response = await axios.post('http://localhost:3001/start', { motion, side, tone });
    setTurns(response.data.turns);
    const aiData = JSON.parse(response.data.aiText);
    setTurns([...response.data.turns, { speaker: 'ai', text: aiData.text }]);
    speak(aiData.text);
  };

  const record = () => {
    // Use MediaRecorder for recording
    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
      const recorder = new MediaRecorder(stream);
      setRecorder(recorder);
      recorder.start();
      setRecording(true);
      recorder.ondataavailable = e => setAudioBlob(e.data);
      recorder.onstop = () => setRecording(false);
    });
  };

  const stopRecord = async () => {
    if (recorder) {
      recorder.stop();
    }
    // Wait for blob to be ready (simplified)
    setTimeout(async () => {
      const formData = new FormData();
      formData.append('file', audioBlob);
      const response = await axios.post('http://localhost:3001/transcribe', formData);
      const userText = response.data.transcript;
      setTurns([...turns, { speaker: 'user', text: userText, audio: audioBlob }]);
      // Get AI response
      const aiResponse = await axios.post('http://localhost:3001/reply', { userText, turns });
      const aiData = JSON.parse(aiResponse.data.text);
      setTurns([...turns, { speaker: 'ai', text: aiData.text }]);
      speak(aiData.text);
    }, 500); // Delay to ensure blob is ready
  };

  const speak = (text) => {
    setAiSpeaking(true);
    let speechText = text;
    try {
      const parsed = JSON.parse(text);
      speechText = parsed.text; // Speak only the text field
    } catch (e) {
      // If not JSON, speak as is
    }
    const utterance = new SpeechSynthesisUtterance(speechText);
    utterance.onend = () => setAiSpeaking(false);
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="App">
      <header>
        <h1>AI Debate Partner</h1>
        <input value={motion} onChange={e => setMotion(e.target.value)} placeholder="Enter debate motion" />
        <select value={side} onChange={e => setSide(e.target.value)}>
          <option value="auto">Auto Assign Side</option>
          <option value="pro">Proposition</option>
          <option value="opp">Opposition</option>
        </select>
        <select value={tone} onChange={e => setTone(e.target.value)}>
          <option>Formal</option><option>Witty</option><option>Aggressive</option><option>Analytical</option>
        </select>
        <button onClick={startDebate}>Start Debate</button>
      </header>
      <div className="chat">
        {turns.map((turn, i) => (
          <div key={i} className={`turn ${turn.speaker}`}>
            <p>{turn.speaker === 'ai' ? (JSON.parse(turn.text).text || turn.text) : turn.text}</p>
            {turn.speaker === 'ai' && <button onClick={() => speak(turn.text)}>üîä Play</button>}
            {turn.speaker === 'user' && turn.audio && <audio controls src={URL.createObjectURL(turn.audio)} />}
          </div>
        ))}
        {aiSpeaking && <div className="ai-speaking">AI is speaking...</div>}
      </div>
      <div className="controls">
        <button onClick={record} disabled={recording} className={recording ? 'recording' : ''}>
          üé§ Record
          {recording && <div className="audio-visualizer">
            <div className="audio-bar"></div>
            <div className="audio-bar"></div>
            <div className="audio-bar"></div>
            <div className="audio-bar"></div>
            <div className="audio-bar"></div>
          </div>}
        </button>
        <button onClick={stopRecord} disabled={!recording}>‚èπÔ∏è Stop & Submit</button>
      </div>
    </div>
  );
}

export default App;
